import React, { Component } from 'react';
import axios from 'axios';
import './HomePage.css';

class HomePage extends Component {



  constructor(props) {
    super(props);

    this.state = {
      planetsList: [],
      password: '',
      error: '',
    };

  }

  dismissError() {
    this.setState({ error: '' });
  }

  handleSubmit(evt) {
    evt.preventDefault();

    if (!this.state.username) {
      return this.setState({ error: 'Username is required' });
    }

    if (!this.state.password) {
      return this.setState({ error: 'Password is required' });
    }

    return this.setState({ error: '' });
  }

  handleUserChange(evt) {
    this.setState({
      username: evt.target.value,
    });
  };

  handlePassChange(evt) {
    this.setState({
      password: evt.target.value,
    });
  }

  fetchDetails(evt){
      alert("working");
  }


  _handleSearchKeyPress(event){

   console.log("key pressed" + event.target.value);

   axios.get('https://swapi.co/api/planets/?search=' + event.target.value)
   .then((response) => {

     if(response.hasOwnProperty("data")){

        let planetsArray = response.data.results;

        for(let _item=0; _item < planetsArray.length; _item++){
          if(planetsArray[_item].population !== "unknown"){
            this.state.planetsList.push(planetsArray[_item]);
          }
     }
     this.state.planetsList.sort((a, b) =>  

           a.population -  b.population

        );


        this.setState({planetsList: planetsArray});
        console.log(JSON.stringify(planetsArray));

     }

   })
   .catch(function (error) {
     console.log(error);
   });

  }

  render() {


    return (
    <div className="container">
        <div className="searchPage">
            <input type="text" id="myInput" placeholder="Search for names.." title="Type in a name" onKeyUp={(evt) => {this._handleSearchKeyPress(evt)}}/>
            <div className="row">
        <div className="col">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Card title</h5>
              <table className="table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>First</th>
                    <th>Last</th>
                    <th>Handle</th>
                  </tr>
                </thead>
                <tbody>

                 {

this.state.planetsList.map((itemObject, _index) => {
    return (
                        <tr key = {_index} onClick={() => this.fetchDetails()} className = "itemRow">
                        <th>1</th>
                        <td>{ itemObject.name }</td>
                        <td>Otto</td>
                        <td> { itemObject.population }</td>
                        </tr>
    )

                 })



                }

                  {/* <tr>
                    <th>2</th>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                  </tr> */}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
        </div>
    </div>
    );

  }
}

export default HomePage;
