import React, { Component } from 'react';
import axios from 'axios';
import { Card, CardHeader, CardText, CardBody,
  CardTitle, Button,CardFooter,CardSubtitle } from 'reactstrap';
import './HomePage.css';

class HomePage extends Component {



  constructor(props) {
    super(props);

    this.state = {
      planetsList: [],
      password: '',
      error: '',
    };

  }

  dismissError() {
    this.setState({ error: '' });
  }

  handleSubmit(evt) {
    evt.preventDefault();

    if (!this.state.username) {
      return this.setState({ error: 'Username is required' });
    }

    if (!this.state.password) {
      return this.setState({ error: 'Password is required' });
    }

    return this.setState({ error: '' });
  }

  handleUserChange(evt) {
    this.setState({
      username: evt.target.value,
    });
  };

  handlePassChange(evt) {
    this.setState({
      password: evt.target.value,
    });
  }

  fetchDetails(evt){
      alert("working");
  }


  _handleSearchKeyPress(event){

   console.log("key pressed" + event.target.value);

   axios.get('https://swapi.co/api/planets/?search=' + event.target.value)
   .then((response) => {

     if(response.hasOwnProperty("data")){

        let planetsArray = response.data.results;
        let sortedArray = [];

        for(let _item=0; _item < planetsArray.length; _item++){
          if(planetsArray[_item].population !== "unknown"){
            sortedArray.push(planetsArray[_item]);
          }
     }
     sortedArray.sort((a, b) =>  

           b.population -  a.population

        );


        this.setState({planetsList: sortedArray});
        console.log(JSON.stringify(sortedArray));

     }

   })
   .catch(function (error) {
     console.log(JSON.stringify(error));
   });

  }

  render() {


    return (
    <div className="container">
        <div className="searchPage">
            <input type="text" id="myInput" placeholder="Search for names.." title="Type in a name" onKeyUp={(evt) => {this._handleSearchKeyPress(evt)}}/>
            <div>
