                <thead>
                  <tr>
                    <th>#</th>
                    <th>First</th>
                    <th>Last</th>
                    <th>Handle</th>
                  </tr>
                </thead>
                <tbody> */}

                 {

this.state.planetsList.map((itemObject, _index) => {
    return (
                        // <tr key = {_index} onClick={() => this.fetchDetails()} className = "itemRow">
                        // <th>1</th>
                        // <td>{ itemObject.name }</td>
                        // <td>Otto</td>
                        // <td> { itemObject.population }</td>
                        // </tr>
                      <div key = {_index} className="cardWrapper">

                          <Card outline color="success">
                          <CardHeader>{ itemObject.name }</CardHeader>
                          <CardBody>
                            <CardTitle>Population: { itemObject.population }</CardTitle>
                            <CardSubtitle>Diameter: { itemObject.diameter }</CardSubtitle>
                            <CardText>Created on: { itemObject.created }</CardText>

                          </CardBody>
                          <CardFooter className="text-muted"><Button color="success">View</Button></CardFooter>
                          </Card>

                      </div>
    )

                 })



                }

                  {/* <tr>
                    <th>2</th>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                  </tr> */}
                {/* </tbody>
              </table>
            </div>
          </div>
        </div> */}
      </div>
        </div>
    </div>
    );

  }
}

export default HomePage;
